$(window).scroll(function () {
  setFixHeader();
});

setFixHeader();

function setFixHeader() {
  var st = $(this).scrollTop();
  if (st > 200) {
    $(".header").addClass("_transform");
  }
  if (st > 250) {
    $(".header").addClass("_fixed-header");
    // $(".header").removeClass("_transform")
  } else {
    $(".header").removeClass("_fixed-header");
    $(".header").removeClass("_transform");
  }
}

