function Sliders() {
  let SlidersArray = [];
  $(".mob-slider").each(function (index, el) {
    $(el).attr("data-slider-id", `${index}`);
    SlidersArray[index] = new Swiper(
      `.mob-slider[data-slider-id="${index}"] .swiper-container`,
      {
        slidesPerView: "auto",
        spaceBetween: 0,
        observer: true,
        observeParents: true,
        observeSlideChildren: true,
        scrollbar: {
          el: `.mob-slider[data-slider-id="${index}"] .swiper-scrollbar`,
          draggable: true,
        },
      }
    );
  });
}

